( function( window, $, undefined ) {

	$(document).ready(function() {
  
	  if ($("#element_2").val() == 1) {
	     $('#li_buttons').hide();
	  }

	    $("#element_2").change(function() {
	      if ( $(this).val() > 1) {
	          $('#li_buttons').show();
	      }
	      else {
	        $('#li_buttons').hide();
	      }
	  });
	});

})( this, jQuery );
